var alsBottomSharer = {
	lang: null,
	getLang: function () {
		if (this.lang) return this.lang;
		var res = 'ru',
			domain = document.location.hostname.toString();
		if (domain.indexOf('artlebedev.com') > -1 || domain.indexOf('artlebedev2.dev') > -1) res = 'en';
		this.lang = res;
		return res;
	},
	translate: function (ru, en) {
		if (this.getLang() == 'ru') return ru;
		return en;
	},
	networks: {
		telegram: {
			url: 'https://t.me/artlebedev'
		},
		vkontakte: {
			url: 'https://vk.com/artlebedev_news'
		},
		instagram: {
			url: 'https://www.instagram.com/artlebedevru/'
		},
		facebook: {
			url: 'https://www.facebook.com/artlebedev.ru/',
		},
		twitter: {
			url: 'https://twitter.com/artlebedev'
		}
	},
	init: function ($c) {
		if (!$c) $c = jQuery('#sh-likes');
		if (!$c.length) return false;
		var self = this;

		if (this.getLang() == 'en') {
			this.networks = {
				facebook: {
					url: 'https://www.facebook.com/artlebedev.ru/',
				},
				instagram: {
					url: 'https://www.instagram.com/artlebedevru/'
				},
				//twitter: {
				//	url: 'https://twitter.com/artlebedev'
				//},
				dribbble: {
					url: 'https://dribbble.com/artlebedevstudio'
				},
				linkedin: {
					url: 'https://www.linkedin.com/company/art.-lebedev-studio/'
				}
			}
		}

		(function($){
			var $main = $('<div />').addClass('sh-main-wrapper');
			$('<span />').addClass('sh-main-wrapper-title').html(self.translate('Мы&nbsp;в&nbsp;соцсетях','Find&nbsp;us on&nbsp;social media')).appendTo($main);
			for(var key in self.networks){
				var n = self.networks[key];
				$('<a />').addClass('sh-main-item').attr('href', n.url).addClass(key).html('<i class="sh-ico sh-ico-' + key + '"></i>').appendTo($main);
			}

			$main.appendTo($c);
		}(jQuery));
	}
};
alsBottomSharer.init();
